            'tchannel.outbound.calls.retries-rejected-by-max-retry-ratio',
            'counter',
            1,
            new stat.OutboundCallsRetriesTags(
                self.serviceName,
                self.callerName,
                self.endpoint,
                self.outReqs.length - 1
