    var channel = this.connection.channel;

    channel.emitFastStat(
        'tchannel.inbound.response.size',
        'counter',
        resFrame.size,
        new stat.InboundResponseSizeTags(
            req ? req.callerName : '',
            req ? req.serviceName : '',
            req ? req.endpoint : ''
