            continue;
        }
        if (!preferIdentified) {
            return conn; // user doesn't care, take last outgoing
        }
        if (conn.remoteName) {
            return conn; // user wanted an identified channel, and we found one
        }
        if (!candidate) {
            candidate = conn; // we'll fallback to returning this if we can't find an identified one
