    this.ipv4 = ipv4;
    this.port = port;
    this.serviceName = serviceName;
}

function Annotation(value, host, timestamp) {
    // TODO: validation
    this.value = value;
    this.timestamp = timestamp || Date.now();
    this.host = host;
}

function BinaryAnnotation(key, value, type, host) {
    // TODO: validation
    this.key = key;
    this.value = value;
    this.type = type ? type : typeof value;
    this.host = host;
