    var self = this;

    // ObjectPool weird free() issue; bail early
    if (!self.channel) {
        return;
    }

    var now = self.channel.timers.now();
    self._observeInboundErrorFrame(now, codeName);

    self.conn.sendLazyErrorFrame(self.id, self.tracing, codeName, message);
